function s(t){if(!t){console.error("Toggle: Button element is required");return}const e=t.getAttribute("href");if(!e){console.error("Toggle: Button must have an href attribute");return}const r=e.startsWith("#")?e.substring(1):e,o=document.getElementById(r);if(!o){console.error(`Toggle: Target element with ID "${r}" not found`);return}const n=t.classList.toggle("active");return o.classList.toggle("active",n),n}export{s as default};
